INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (888, 1, 'Der <em>Glanzglobus</em> der Lampe reflektiert das Licht wunderbar.', 'The <em>glossy globe</em> of the lamp wonderfully reflects the light.');
