INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (897, 1, 'Der <em>Gleichgewichtsverlust</em> kann auf eine Erkrankung des Innenohrs hindeuten.', 'The <em>loss of balance</em> may indicate an inner ear disorder.');
