INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (901, 1, '<em>Glücklicherweise</em> waren wir rechtzeitig am Flughafen.', '<em>Fortunately</em>, we arrived at the airport on time.');
