INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (901, 4, '<em>Glücklicherweise</em> fand sie das verlorene Geld wieder.', '<em>By good luck</em>, she found the lost money again.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (902, 1, 'Die Gnade Gottes<em> scheint</em> über sie zu wirken.', 'The <em>grace</em> of God seems to be working over her.');
