INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (902, 4, 'Die<em> Gnade</em> der Vergebung ist unendlich.', 'The <em>forgiveness</em> of grace is infinite.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (903, 1, 'Der <em>gnadenlose</em> Sturm zerstörte die Außenanlagen.', 'The <em>merciless</em> storm destroyed the exterior facilities.');
