INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1340, 1, 'Eine <em>Maxime</em> für seine Politik war: <em>Ehrlichkeit</em> und <em>Transparenz</em>.', 'A <em>principle</em> for his politics was: <em>honesty</em> and <em>transparency</em>.');
