INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1341, 1, 'Die Regierung hat strenge <em>Maßnahmen</em> zur Bekämpfung der Pandemie ergriffen.', 'The government has taken strict <em>measures</em> to combat the pandemic.');
