INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1440, 1, 'Das <em>Nichtvorhandensein</em> von Beweisen erschwert die Ermittlungen.', 'The <em>non-existence</em> of evidence complicates the investigations.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1440, 2, 'Das <em>Nichtvorhandensein</em> geeigneter Alternativen ist problematisch.', 'The <em>absence</em> of suitable alternatives is problematic.');
