INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1441, 1, 'Er <em>schlug</em> den Gegner im ersten Round <em>niedergeschlagen</em>.', 'He <em>knocked down</em> his opponent in the first round.');
