INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1444, 1, 'Die Soldaten mussten durch das <em>Niemandsland</em> zwischen den Frontlinien gehen.', 'The soldiers had to cross the <em>no man''s land</em> between the front lines.');
