INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1444, 3, 'Nach dem Streit herrschte ein <em>Niemandsland</em> zwischen den beiden Freunden.', 'After the argument, there was a <em>no-man''s-land</em> between the two friends.');
