INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1451, 3, 'Die <em>nutzlosen</em> Informationen machten die Sache nur komplizierter.', 'The <em>ineffective</em> information only made things more complicated.');
