INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1456, 1, 'Die Schüler <em>rückten</em> im Unterricht <em>näher</em> zur Tafel <em>zurück</em>.', 'The students <em>moved closer</em> to the blackboard during the lesson.');
