INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1619, 1, 'Der Wind formte einen hohen <em>Sandhügel</em> am Strand.', 'The wind shaped a tall <em>sand dune</em> on the beach.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1619, 2, 'Sie baute einen <em>Sandhügel</em> im Spielkasten.', 'She built a <em>mound of sand</em> in the sandbox.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1619, 3, 'Der <em>Sandhügel</em> hinter dem Haus war perfekt zum Spielen.', 'The <em>heap of sand</em> behind the house was perfect for playing.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1620, 1, 'Der Strand ist <em>sandig</em> und warm.', 'The beach is <em>sandy</em> and warm.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1620, 2, 'Das <em>sandige</em> Wasser war kaum zu sehen.', 'The <em>grainy</em> water was hardly visible.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1620, 3, 'Die <em>sandige</em> Straße führte zu ihrem Haus.', 'The <em>dusty</em> road led to their house.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1621, 1, 'Der Strand hier ist <em>sandiger</em> als der letzte.', 'The beach here is <em>sandy</em> (more sandy) than the last one.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1621, 2, 'Diese Wintersportanlage hatte ein <em>sandigeres</em> Gefühl als ich erwartet hatte.', 'This winter sports facility had a <em>more sand-like</em> feel than I had expected.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1621, 3, 'Der Belag der Gleise schien <em>sandiger</em> geworden zu sein.', 'The surface of the tracks seemed to have become <em>more grainy</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1622, 1, 'Die <em>Sandkaskade</em> im Garten ist ein wunderschöner Anblick.', 'The <em>sand cascade</em> in the garden is a beautiful sight.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1622, 2, 'Die Kinder spielten begeistert mit der <em>Sandkaskade</em> am Strand.', 'The children played excitedly with the <em>sand waterfall</em> at the beach.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1623, 1, 'Jedes <em>Sandkorn</em> am Strand glänzte in der Sonne.', 'Each <em>grain of sand</em> on the beach shone in the sun.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1623, 2, 'Die <em>Sandkörner</em> sind durch den Wind geformt worden.', 'The <em>sand grains</em> have been shaped by the wind.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1623, 3, 'Ein <em>Sandkorn</em> fiel ihm in das Auge.', 'A <em>particle of sand</em> fell into his eye.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1624, 1, 'Die Kinder spielen im <em>Sandloch</em>.', 'The children are playing in the <em>sandpit</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1624, 2, 'Der Hund hat ein <em>Sandloch</em> gegraben.', 'The dog dug a <em>sand hole</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1625, 1, 'Das <em>Sandmeer</em> sah nach einem perfekten Ort zum Graben aus.', 'The <em>sand pit</em> looked like a perfect place to dig.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1625, 2, 'Die Kinder spielten vergnügt im <em>Sandmeer</em>.', 'The children played happily in the <em>sand pool</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1625, 3, 'Vor uns erstreckte sich ein <em>Sandmeer</em> ohne Ende.', 'Before us lay an endless <em>sea of sand</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1626, 1, 'Die <em>Sandmeere</em> erstreckten sich bis zum Horizont.', 'The <em>sand seas</em> stretched out to the horizon.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1626, 2, 'Die <em>Sandmeere</em> der Wüste schimmerten in der Sonne.', 'The <em>seas of sand</em> in the desert shimmered in the sun.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1627, 1, 'Der <em>Sandrutsch</em> an der Küste wird <em>immer</em> gefährlicher.', 'The <em>sand slip</em> at the coast is becoming <em>ever</em> more dangerous.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1627, 2, 'Der <em>Sandrutsch</em> verursachte massive Schäden an den Häusern.', 'The <em>landslide</em> caused massive damage to the houses.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1628, 1, 'Wir <em>sanddrücken</em> das Moos zwischen den Steinen.', 'We <em>press sand</em> (into) the moss between the stones.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1628, 2, 'Sie <em>sanddrückte</em> die feine Erde in die Ritzen.', 'She <em>squeezed</em> the fine soil <em>into</em> the cracks.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1628, 3, 'Man muss den Untergrund gut <em>sanddrücken</em>.', 'You have to <em>pack the sand</em> well in the base.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1628, 4, 'Nach dem Gießen <em>sanddrückt</em> er das Beet fest.', 'After watering, he <em>tamps down</em> the flowerbed.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1629, 1, 'Der <em>Sandsturm</em> kam plötzlich auf und machte das Fahren gefährlich.', 'The <em>sandstorm</em> suddenly arose and made driving dangerous.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1629, 2, 'Ein schwerer <em>Sandsturm</em> zog über die Wüste.', 'A heavy <em>dust storm</em> swept across the desert.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1629, 3, 'Die <em>Sandstürme</em> in der Wüste können sehr intensiv sein.', 'The <em>sandstorms</em> in the desert can be very intense.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1629, 4, '<em>Sandstürme</em> können Sichtweiten stark reduzieren.', '<em>Dust storms</em> can severely reduce visibility.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1630, 1, 'Die <em>Sandstürme</em> in der Wüste sind oft sehr gefährlich.', 'The <em>sandstorms</em> in the desert are often very dangerous.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1630, 2, '<em>Sandstürme</em> können die Sicht auf mehrere Kilometer beeinträchtigen.', '<em>Sand storms</em> can impair visibility for several kilometers.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1631, 1, 'Die <em>Sandwand</em> in der Höhle war sehr beeindruckend.', 'The <em>sand wall</em> in the cave was very impressive.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1631, 2, 'Die <em>Sandwände</em> am Strand sind hoch und steil.', 'The <em>sand dunes</em> at the beach are tall and steep.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1631, 3, 'Die Flüsse waren durch <em>Sandwände</em> voneinander getrennt.', 'The rivers were separated by <em>sand banks</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1631, 4, 'Er kletterte vorsichtig die <em>Sandwand</em> hinunter.', 'He carefully climbed down the <em>sand cliff</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1632, 1, 'Die <em>Sandwelle</em> erstreckte sich über den ganzen Fluss.', 'The <em>sandbar</em> extended across the entire river.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1632, 2, 'Die <em>Sandwellen</em> am Strand wurden durch die Gezeiten gebildet.', 'The <em>sand waves</em> on the beach were formed by the tides.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1632, 3, 'Booten fielen oft über die <em>Sandwellen</em> des alten Flusses.', 'Boats often got stuck on the <em>sand ridges</em> of the old river.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1633, 1, 'Die <em>Sandwoge</em> bewegte sich langsam durch die Wüste.', 'The <em>sand wave</em> moved slowly through the desert.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1633, 2, 'Die <em>Sandwoge</em> wurde durch die Strömung geformt.', 'The <em>sandbank</em> was shaped by the current.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1634, 1, 'Eine <em>Sandwolke</em> sei in der Wüste aufgezogen.', 'A <em>sand cloud</em> had risen in the desert.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1634, 2, 'Die <em>Sandwolke</em> verdeckte die Sicht.', 'The <em>cloud of sand</em> obscured visibility.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1634, 3, 'Die <em>Sandwolke</em> wurde durch den starken Wind verursacht.', 'The <em>dust cloud</em> was caused by the strong wind.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1635, 1, 'Er hielt das <em>sanftere</em> Ende des Stocks in die Höhe.', 'He raised the <em>gentler</em> end of the stick.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1635, 2, 'Ich brauche ein <em>sanfteres</em> Kopfkissen für besseren Schlaf.', 'I need a <em>softer</em> pillow for better sleep.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1635, 3, 'Das <em>sanftere</em> Wetter erlaubte uns einen längeren Spaziergang.', 'The <em>milder</em> weather allowed us to take a longer walk.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1635, 4, 'Sie gab ihm einen <em>sanfteren</em> Blick.', 'She gave him a <em>tenderer</em> look.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1636, 1, 'Das <em>Sang</em> der Vögel war angenehm.', 'The birds'' <em>singing</em> was pleasant.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1636, 2, 'Sein <em>Sang</em> erfüllte den Raum.', 'His <em>song</em> filled the room.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1636, 3, 'Er hat das ganze Leben <em>gesungen</em>.', 'He has <em>sung</em> for his entire life.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1636, 4, 'Die Nachtigall <em>singt</em> jeden Morgen.', 'The nightingale <em>sings</em> every morning.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1637, 1, 'Das Schiff <em>sank</em> nach dem Unfall schnell.', 'The ship <em>sank</em> quickly after the accident.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1637, 2, 'Die Sonne <em>sank</em> hinter den Bergen.', 'The sun <em>sank</em> behind the mountains.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1637, 3, 'Der Temperatursensor <em>sank</em> unter null.', 'The temperature sensor <em>went down</em> below zero.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1637, 4, 'Der Aktienkurs <em>sank</em> drastisch.', 'The stock price <em>dropped</em> drastically.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1638, 1, 'Der <em>Sauerstoff</em> in der Luft ist essenziell für das Leben.', 'The <em>oxygen</em> in the air is essential for life.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1638, 2, 'Die Pflanzen produzieren <em>Sauerstoff</em> durch Photosynthese.', 'Plants produce <em>oxygen</em> through photosynthesis.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1638, 3, 'Ohne <em>Sauerstoff</em> kann ein Feuer nicht brennen.', 'Without <em>oxygen</em>, a fire cannot burn.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1639, 1, 'Die Pferde <em>saufen</em> an der Tränke.', 'The horses <em>drink (like animals)</em> at the trough.');
