INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1639, 3, 'Sie <em>saufen</em> oft zu viel.', 'They <em>often drink heavily</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1639, 4, 'Die Schwämme <em>saugen</em> Wasser auf.', 'The sponges <em>soak up</em> water.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1639, 5, 'Die Stadt <em>sauft</em> bei jedem Regen unter.', 'The town <em>drowns</em> in water with every rain.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1640, 1, 'Er <em>schafft</em> es, rechtzeitig zu kommen.', 'He <em>manages</em> to arrive on time.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1640, 2, 'Sie <em>schafft</em> ein wunderschönes Gemälde.', 'She <em>creates</em> a beautiful painting.');
