INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1641, 1, 'Nachdem er hart gearbeitet hatte, <em>schaffte</em> er es endlich, das Projekt abzuschließen.', 'After he worked hard, he finally <em>managed</em> to complete the project.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1641, 2, 'Trotz aller Schwierigkeiten <em>schaffte</em> sie es, das Rennen zu gewinnen.', 'Despite all the difficulties, she <em>succeeded</em> in winning the race.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1641, 3, 'Die neuen Gesetze <em>schafften</em> wichtige Veränderungen im Bildungswesen.', 'The new laws <em>established</em> important changes in education.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1641, 4, 'Er <em>schaffte</em> eine angenehme Atmosphäre im Büro.', 'He <em>created</em> a pleasant atmosphere in the office.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1642, 1, 'Sie <em>schafften</em> es, die Prüfung zu bestehen.', 'They <em>managed</em> to pass the exam.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1642, 2, 'Die Künstler <em>schafften</em> ein beeindruckendes Gemälde.', 'The artists <em>created</em> an impressive painting.');
