INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1643, 1, 'Er schaltete mit dem <em>Schalthebel</em> in den nächsten Gang.', 'He shifted into the next gear with the <em>gear lever</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1643, 2, 'Der <em>Schalthebel</em> in ihrem Auto war kaum zu erreichen.', 'The <em>shift lever</em> in her car was barely reachable.');
