INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1645, 1, 'Die <em>Schattengebiete</em> der Pflanzen müssen regelmäßig überprüft werden.', 'The <em>shadow areas</em> of the plants need to be checked regularly.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1645, 2, 'In den <em>Schattengebieten</em> des Gebäudes leben oft Tiere.', 'In the <em>shadow zones</em> of the building, animals often live.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1645, 3, 'Das Auto wurde im <em>Schattengebiet</em> geparkt, um vor der Sonne geschützt zu sein.', 'The car was parked in the <em>dark area</em> to protect it from the sun.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1646, 1, 'Sie <em>schaut</em> aus dem Fenster.', 'She <em>looks</em> out of the window.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1646, 2, 'Die <em>Schau</em> war unglaublich spannend.', 'The <em>show</em> was incredibly exciting.');
