INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1647, 1, 'Sie <em>schauderte</em> bei dem Gedanken an die Dunkelheit.', 'She <em>shuddered</em> at the thought of the darkness.');
