INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1651, 1, 'Der Kaffee hatte <em>einen</em> dicken <em>Schaum</em>.', 'The coffee had a thick <em>foam</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1651, 2, 'Das Bier war nach dem Einschenken mit <em>Schaum</em> bedeckt.', 'The beer was covered with <em>froth</em> after being poured.');
