INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1663, 1, 'Die <em>Schildexplosion</em> erzeugte ein gleißendes Licht.', 'The <em>shield explosion</em> created a blinding light.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1663, 2, 'Nach dem Treffer kam es zu einer gewaltigen <em>Schildexplosion</em>.', 'After the hit, there was a huge <em>shield burst</em>.');
