INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1667, 1, 'Die Ruhe des <em>Schlafes</em> ist wichtig für die Gesundheit.', 'The <em>rest of sleep</em> is important for health.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1667, 2, 'Die Stille <em>des Schlafes</em> erfüllte den Raum.', 'The silence <em>of slumber</em> filled the room.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1667, 3, 'Er erholte sich <em>im Schlafes</em> eines langen Nachts.', 'He recovered <em>in dreamless sleep</em> after a long night.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1668, 1, 'Die <em>Schlaffheit</em> nach dem Mittagessen war überwältigend.', 'The <em>sleepiness</em> after lunch was overwhelming.');
