INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1680, 1, 'Nach intensiven Gesprächen kam er zu einer <em>Schlussfolgerung</em>.', 'After intense discussions, he came to a <em>conclusion</em>.');
