INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1682, 1, 'Die <em>schmalen</em> Gassen des alten Viertels sind charmant.', 'The <em>narrow</em> alleys of the old quarter are charming.');
