INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1684, 1, 'Er <em>schmeichelte</em> ihr mit Komplimenten, um ihre Zuneigung zu gewinnen.', 'He <em>flattered</em> her with compliments to win her affection.');
