INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1684, 3, 'Sie <em>schmeichelte</em> ihrem Chef, um eine Beförderung zu bekommen.', 'She <em>buttered up</em> her boss to get a promotion.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1684, 4, 'Er <em>schmeichelte</em> sich bei der Lehrerin ein, um bessere Noten zu bekommen.', 'He <em>ingratiated himself</em> with the teacher to get better grades.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1685, 1, 'Die <em>Schmerzen</em> in meinem Kopf waren unerträglich.', 'The <em>pains</em> in my head were unbearable.');
