INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1687, 1, 'Der <em>Schmuggler</em> wurde an der Grenze festgenommen.', 'The <em>smuggler</em> was arrested at the border.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1687, 2, 'Sie arbeitete als <em>Schmugglerin</em> für illegale Waren.', 'She worked as a <em>trafficker</em> for illegal goods.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1687, 3, 'Die <em>Schmuggler</em> brachten Alkohol während der Prohibition ins Land.', 'The <em>bootleggers</em> brought alcohol into the country during Prohibition.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1688, 1, 'Die <em>Schmugglern</em> wurden von der Polizei gefasst.', 'The <em>smugglers</em> were caught by the police.');
