INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1690, 1, 'Er <em>schnappte</em> den Ball aus der Luft.', 'He <em>grabbed</em> the ball out of the air.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1690, 2, 'Die Katze <em>schnappte</em> nach dem Spielzeug.', 'The cat <em>snapped</em> at the toy.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1690, 3, 'Sie <em>schnappte</em> das Angebot sofort auf.', 'She <em>caught</em> the opportunity immediately.');
