INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1690, 5, 'Das Radio <em>schnappte</em> ein Signal.', 'The radio <em>picked up</em> a signal.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1691, 1, 'Die Aktien <em>schnellten</em> nach der guten Nachricht in die Höhe.', 'The stocks <em>shot up</em> after the good news.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1691, 2, 'Die Katze <em>schnellte</em> plötzlich vom Tisch.', 'The cat suddenly <em>sprang</em> off the table.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1691, 3, 'Der Wasserstrahl <em>schnellte</em> aus dem Schlauch.', 'The jet of water <em>jumped</em> out of the hose.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1691, 4, 'Sie <em>schnellte</em> mit ihrem Auto durch die Straße.', 'She <em>sped</em> through the street in her car.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1692, 1, 'Die neue Internetverbindung ist <em>schneller</em> als die alte.', 'The new internet connection is <em>faster</em> than the old one.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1692, 2, 'Er läuft <em>schneller</em> als je zuvor.', 'He is running <em>quicker</em> than ever before.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1692, 3, 'Bitte erledige die Aufgabe <em>schneller</em>!', 'Please complete the task <em>more quickly</em>!');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1693, 1, 'Er <em>schnippte</em> mit den Fingern und die Musik begann.', 'He <em>snapped</em> his fingers and the music started.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1693, 2, 'Sie <em>schnippte</em> das Wasser von ihren Händen.', 'She <em>flicked</em> the water from her hands.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1693, 3, 'Der Junge <em>schnippte</em> die Fliege von seinem Teller.', 'The boy <em>flicked away</em> the fly from his plate.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1694, 1, 'Der Hund <em>schnuppert</em> am Boden.', 'The dog is <em>sniffing</em> the ground.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1694, 2, 'Sie <em>schnupperte</em> am Parfüm.', 'She <em>took a whiff</em> of the perfume.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1694, 3, 'Er <em>schnupperte</em> in den neuen Job hinein.', 'He <em>tried out</em> the new job.');
