INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1695, 1, 'Die Nachricht <em>schockierte</em> alle Anwesenden.', 'The news <em>shocked</em> everyone present.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1695, 2, 'Ihr Verhalten <em>schockierte</em> selbst ihre besten Freunde.', 'Her behavior <em>appalled</em> even her closest friends.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1695, 3, 'Die Ergebnisse der Studie <em>schockierten</em> die Wissenschaftler.', 'The results of the study <em>astonished</em> the scientists.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1696, 1, 'Der <em>Schrecken</em> breitete sich über das ganze Land aus.', 'The <em>terror</em> spread across the entire country.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1696, 2, 'Sie hatte einen <em>Schrecken</em> bekommen, als der Hund plötzlich bellte.', 'She had been given a <em>fright</em> when the dog suddenly barked.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1696, 3, 'Der <em>Schrecken</em> der Nachricht war kaum zu fassen.', 'The <em>shock</em> of the news was hard to comprehend.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1696, 4, 'Der <em>Schrecken</em> der Vergangenheit verfolgte ihn noch immer.', 'The <em>dread</em> of the past still haunted him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1697, 1, 'Die <em>Schreckens</em>nachrichten machten sie krank.', 'The <em>terrors</em> of the news made her sick.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1697, 2, 'Ihre <em>Schreckens</em>visionen hinderten sie am Schlafen.', 'Her <em>fears</em> prevented her from sleeping.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1697, 3, 'Er blieb vor den <em>Schreckens</em>bildern der Kriegsreportage stehen.', 'He stopped in front of the <em>horrors</em> depicted in the war report.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1697, 4, 'Die <em>Schreckens</em>geschichten verfolgten ihn in seinen Träumen.', 'The <em>nightmares</em> from the scary stories haunted him in his dreams.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1698, 1, 'Ich <em>schrieb</em> einen Brief.', 'I <em>wrote</em> a letter.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1698, 2, 'Er <em>schrieb</em> fleißig an seinem Roman.', 'He <em>wrote</em> diligently on his novel.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1698, 3, 'Als ich ankam, <em>schrieb</em> sie gerade.', 'When I arrived, she <em>was writing</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1698, 4, 'Sie <em>schrieb</em> die wichtige Telefonnummer schnell auf.', 'She quickly <em>wrote down</em> the important phone number.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1699, 1, 'Er machte einen <em>Schritt</em> nach vorne.', 'He took a <em>step</em> forward.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1699, 2, 'Sie hörte <em>Schritte</em> im Flur.', 'She heard <em>footsteps</em> in the hallway.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1699, 3, 'Wir müssen einen <em>Schritt</em> in die richtige Richtung machen.', 'We need to take a <em>move</em> in the right direction.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1700, 1, 'Sie hat große <em>Schulden</em> bei der Bank.', 'She has large <em>debts</em> with the bank.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1700, 2, 'Er trägt die <em>Schuld</em> für den Unfall.', 'He bears the <em>guilt</em> for the accident.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1700, 3, 'Es war nicht meine <em>Schuld</em>.', 'It wasn''t my <em>fault</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1701, 1, 'Sie hat große <em>Schulden</em> bei der Bank.', 'She has a large <em>debt</em> with the bank.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1701, 2, 'Er hat seine <em>Schulden</em> endlich beglichen.', 'He finally settled his <em>debts</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1701, 3, 'Unternehmer müssen ihre <em>Schulden</em> im Blick behalten.', 'Entrepreneurs need to keep their <em>liabilities</em> in check.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1702, 1, 'Die <em>Schule</em> beginnt um acht Uhr.', 'The <em>school</em> starts at eight o''clock.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1702, 2, 'Die <em>Schule</em> ist sehr alt.', 'The <em>school</em> is very old.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1703, 1, 'Die <em>schwache</em> Batterie muss ersetzt werden.', 'The <em>weak</em> battery needs to be replaced.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1703, 2, 'Er hatte eine <em>schwache</em> Stimme.', 'He had a <em>feeble</em> voice.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1703, 3, 'Ein <em>schwaches</em> Licht leuchtete in der Ferne.', 'A <em>faint</em> light shone in the distance.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1703, 4, 'Die Leistung des Teams war <em>schwach</em>.', 'The team''s performance was <em>poor</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1704, 1, 'Das ist <em>Schwachsinn</em>!', 'That''s <em>nonsense</em>!');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1704, 2, 'Er redet nur <em>Schwachsinn</em>.', 'He''s just talking <em>bullshit</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1704, 3, 'Das ganze Konzept erscheint mir wie <em>Schwachsinn</em>.', 'The whole concept seems like <em>rubbish</em> to me.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1704, 4, 'Wir sollten diesen <em>Schwachsinn</em> nicht ernst nehmen.', 'We shouldn''t take this <em>foolishness</em> seriously.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1705, 1, 'Der <em>Schwall</em> Wasser fiel aus dem überlaufenden Becken.', 'The <em>splash</em> of water poured from the overflowing basin.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1705, 2, 'Ein <em>Schwall</em> von Emotionen überkam ihn.', 'A <em>flood</em> of emotions overcame him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1705, 3, 'Der <em>Schwall</em> an neuen Ideen war überwältigend.', 'The <em>surge</em> of new ideas was overwhelming.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1705, 4, 'Er sprach mit einem <em>Schwall</em> von Worten.', 'He spoke with a <em>rush</em> of words.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1706, 1, 'Die', 'The');
