INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1707, 1, 'Die Temperatur <em>schwankt</em> zwischen 20 und 30 Grad.', 'The temperature <em>fluctuates</em> between 20 and 30 degrees.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1707, 2, 'Der Baum <em>schwankt</em> im Wind.', 'The tree <em>sways</em> in the wind.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1707, 3, 'Er <em>schwankt</em> zwischen zwei Entscheidungen.', 'He <em>wavers</em> between two decisions.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1707, 4, 'Sie <em>schwankte</em> nach dem langen Lauf.', 'She <em>staggered</em> after the long run.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1708, 1, 'Die Libelle <em>schwebt</em> über dem Wasser.', 'The dragonfly <em>hovers</em> over the water.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1708, 2, 'Der Ballon <em>schwebt</em> in der Luft.', 'The balloon <em>floats</em> in the air.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1708, 3, 'Eine Idee <em>schwebt</em> in meinem Kopf.', 'An idea <em>drifts</em> in my mind.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1708, 4, 'Eine angenehme Atmosphäre <em>schwebt</em> im Raum.', 'A pleasant atmosphere <em>lingers</em> in the room.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1709, 1, 'Nach dem Streit <em>schweig</em>en sie beide.', 'After the argument, they both <em>fell silent</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1709, 2, 'Sie <em>schweigt</em> lieber, als zu streiten.', 'She <em>would rather remain silent</em> than argue.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1709, 3, 'Er <em>schweigt</em> über seine Gefühle.', 'He <em>keeps quiet</em> about his feelings.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1709, 4, 'Die Zeugen <em>schweigen</em> aus Angst.', 'The witnesses <em>remain silent</em> out of fear.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1710, 1, 'Sie hörte die <em>schweigende</em> Menge.', 'She listened to the <em>silent</em> crowd.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1710, 2, 'Er saß <em>schweigend</em> am Tisch.', 'He sat <em>quietly</em> at the table.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1710, 3, 'Der <em>schweigende</em> Protest wurde schließlich laut.', 'The <em>muttered</em> protest eventually became loud.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1711, 1, 'Trotz der Fragen <em>schwieg</em> sie.', 'Despite the questions, she <em>remained silent</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1711, 2, 'Er <em>schweigt</em> seit Stunden.', 'He has <em>remained silent</em> for hours.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1711, 3, 'Nach dem Streit <em>schwieg</em> er.', 'After the argument, he <em>stopped talking</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1711, 4, 'Sie wollte <em>schweigen</em> und ihn nicht verletzen.', 'She wanted to <em>say nothing</em> and not hurt him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1712, 1, 'Die <em>Schwerfälligkeit</em> seiner Bewegungen machte es offensichtlich, dass er unwohl fühlte.', 'The <em>clumsiness</em> of his movements made it obvious that he was feeling unwell.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1712, 2, 'Die <em>Schwerfälligkeit</em> der Maschine führte zu einem langsamen Produktionsprozess.', 'The <em>sluggishness</em> of the machine led to a slow production process.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1712, 3, 'Die <em>Schwerfälligkeit</em> des Themas belastete die Gespräche.', 'The <em>heaviness</em> of the topic weighed down the discussions.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1712, 4, 'Die <em>Schwerfälligkeit</em> des Textes machte ihn schwer verständlich.', 'The <em>ponderousness</em> of the text made it difficult to understand.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1713, 1, 'Das <em>Schwert</em> lag in der Ecke.', 'The <em>sword</em> lay in the corner.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1713, 2, 'Er zog sein <em>Schwert</em> aus der Scheide.', 'He drew his <em>blade</em> from the sheath.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1713, 3, 'Das <em>Schwert</em> der Gerechtigkeit.', 'The <em>sword</em> of justice.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1714, 1, 'Mein <em>Schwester</em> lebt in Berlin.', 'My <em>sister</em> lives in Berlin.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1714, 2, 'Die <em>Schwester</em> hilft dem Arzt.', 'The <em>nurse</em> helps the doctor.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1714, 3, 'Ich habe drei <em>Schwestern</em>.', 'I have three <em>siblings</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1714, 4, 'Sie ist meine <em>Schwester</em> durch Heirat.', 'She is my <em>sister-in-law</em> by marriage.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1715, 1, 'Nach dem Streit <em>schwieg</em> sie lange.', 'After the argument, she <em>remained silent</em> for a long time.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1715, 2, 'Als er die Wahrheit hörte, <em>schwieg</em> er plötzlich.', 'When he heard the truth, he suddenly <em>fell silent</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1715, 3, 'Er <em>schwieg</em> über seine Pläne.', 'He <em>kept quiet</em> about his plans.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1715, 4, 'Sie <em>schwieg</em> aus Angst vor Konsequenzen.', 'She <em>didn''t say a word</em> out of fear of consequences.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1716, 1, 'Mathematik funktioniert manchmal nicht, weil <em>Schwierigkeiten</em> auftreten.', 'Mathematics doesn''t always work because <em>difficulties</em> arise.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (1716, 2, 'Zusammenarbeiten kann <em>Schwierigkeiten</em> mit sich bringen.', 'Working together can bring <em>difficulties</em>.');
