INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2009, 3, 'Ihr <em>unregelmäßiges</em> Verhalten sorgte für Besorgnis bei ihren Freunden.', 'Her <em>erratic</em> behavior caused concern among her friends.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2010, 1, 'Das Muster war <em>unregelmäßig</em> und chaotisch.', 'The pattern was <em>irregular</em> and chaotic.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2010, 2, 'Der Boden war <em>unregelmäßig</em> und steinig.', 'The ground was <em>uneven</em> and stony.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2010, 3, 'Er hatte <em>unregelmäßige</em> Schlafgewohnheiten.', 'He had <em>erratic</em> sleeping habits.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2010, 4, 'Die <em>unregelmäßigen</em> Schmerzen machten ihm zu schaffen.', 'The <em>intermittent</em> pain troubled him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2011, 1, 'Die <em>unregelmäßigen</em> Abstände machten es schwierig, die Verbindung zu erkennen.', 'The <em>irregular</em> intervals made it difficult to recognize the connection.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2011, 2, 'Die <em>unregelmäßigen</em>  Luftblasen in der Flüssigkeit deuteten auf ein Problem hin.', 'The <em>uneven</em> air bubbles in the liquid indicated a problem.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2011, 3, 'Er zeigt <em>unregelmäßige</em> Fortschritte in seinem Training.', 'He shows <em>erratic</em> progress in his training.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2012, 1, 'Das gehört <em>unserem</em> Sohn.', 'That belongs to <em>our</em> son.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2012, 2, 'Wir helfen <em>unserem</em> Freund.', 'We help <em>our</em> friend.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2012, 3, 'Gib <em>unserem</em> Hund das Spielzeug.', 'Give the toy to <em>our</em> dog.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2013, 1, 'Wir haben <em>unseren</em> Hund gestern gefüttert.', 'We fed <em>our</em> dog yesterday.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2013, 2, 'Das ist der Wert <em>unserer</em> Manufaktur.', 'That is the value <em>of our</em> manufactory.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2013, 3, 'Hoffentlich finden wir <em>unseren</em> wieder.', 'Hopefully, we''ll find <em>our</em> (thing) again.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2014, 1, 'Das ist <em>unseres</em> Vaters Auto.', 'That is <em>our</em> father''s car.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2014, 2, 'Das Buch ist <em>unseres</em>.', 'The book is <em>ours</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2014, 3, '<em>Unseres</em> wird bald fertig sein.', '<em>Ours</em> will be ready soon.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2014, 4, 'Die Schönheit <em>unseres</em> Landes.', 'The beauty of <em>our</em> country.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2015, 1, 'Die <em>unsichtbare</em> Tinte ist nur mit einer speziellen Lampe sichtbar.', 'The <em>invisible</em> ink is only visible with a special lamp.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2015, 2, 'Die <em>unsichtbaren</em> Fäden hielten das ganze Kunstwerk zusammen.', 'The <em>invisible</em> threads held the entire artwork together.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2015, 3, 'Sie glaubte, der <em>Unsichtbare</em> beobachte sie.', 'She believed that the <em>invisible person</em> was watching her.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2015, 4, 'Der <em>Unsichtbare</em> in unserem Leben zeigt, wie wenig wir manchmal sehen.', 'The <em>phantom</em> in our lives shows how little we sometimes see.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2016, 1, 'Im <em>unterbewußten</em> Teil unseres Geistes nehmen wir viele Eindrücke wahr.', 'In the <em>subconscious</em> part of our mind, we perceive many impressions.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2016, 2, 'Das <em>unterbewußte</em> Gefühl ließ ihn nicht los.', 'The <em>subconscious</em> feeling wouldn''t leave him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2017, 1, 'Der Lehrer <em>unterbricht</em> den Schüler, um eine Frage zu stellen.', 'The teacher <em>interrupts</em> the student to ask a question.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2017, 2, 'Sie <em>unterbrach</em> den Kontakt zu ihren Freunden.', 'She <em>broke off</em> contact with her friends.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2017, 3, 'Wir müssen die Besprechung kurz <em>unterbrechen</em>.', 'We need to <em>suspend</em> the meeting for a moment.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2018, 1, 'Die Regierung <em>unterdrückt</em> die oppositionellen Stimmen.', 'The government <em>suppresses</em> opposing voices.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2018, 2, 'Sie <em>unterdrückte</em> ihre Gefühle, um nicht zu weinen.', 'She <em>repressed</em> her feelings to avoid crying.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2018, 3, 'Der Lärm <em>unterdrückte</em> sein konzentriertes Arbeiten.', 'The noise <em>stifled</em> his focused work.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2018, 4, 'Der Polizist <em>unterdrückte</em> die Proteste gewaltsam.', 'The police officer <em>put down</em> the protests violently.');
