INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2020, 1, 'Das Schiff ist im Sturm <em>untergegangen</em>.', 'The ship <em>sank</em> in the storm.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2020, 2, 'Die Sonne ist am Horizont <em>untergegangen</em>.', 'The sun <em>went down</em> on the horizon.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2020, 3, 'Viele Ideen <em>sind untergegangen</em>.', 'Many ideas have <em>perished</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2020, 4, 'Seine Pläne <em>sind</em> leider <em>untergegangen</em>.', 'Unfortunately, his plans have <em>failed</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2021, 1, 'Die Clowns <em>unterhalten</em> die Kinder.', 'The clowns <em>entertain</em> the children.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2021, 2, 'Sie <em>unterhielten</em> sich stundenlang.', 'They <em>conversed</em> for hours.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2021, 3, 'Er <em>unterhält</em> seinen alten Wagen gut.', 'He <em>maintains</em> his old car well.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2022, 1, 'Er hat gestern ein interessantes Projekt <em>unternommen</em>.', 'He <em>undertook</em> an interesting project yesterday.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2022, 2, 'Sie <em>unternehmt</em> viel, um die Umwelt zu schützen.', 'They <em>take</em> a lot of <em>action</em> to protect the environment.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2022, 3, 'Wir haben heute viel <em>unternommen</em>.', 'We <em>did</em> a lot today.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2023, 1, 'Der <em>Unterschied</em> zwischen den beiden Autos ist deutlich sichtbar.', 'The <em>difference</em> between the two cars is clearly visible.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2023, 2, 'Es gab einen <em>Unterschied</em> in den beiden Berichten.', 'There was a <em>discrepancy</em> in the two reports.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2023, 3, 'Er macht einen <em>Unterschied</em> zwischen Fakt und Meinung.', 'He makes a <em>distinction</em> between fact and opinion.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2024, 1, 'Die Bücher decken <em>die unterschiedlichsten</em> Themen ab.', 'The books cover <em>the most varied</em> topics.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2024, 2, 'Er sammelt die <em>unterschiedlichsten</em> Münzen.', 'He collects the <em>most diverse</em> coins.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2024, 3, 'Sie hatte <em>die unterschiedlichsten</em> Ideen.', 'She had <em>the widest variety</em> of ideas.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2024, 4, 'Er trug <em>die unterschiedlichsten</em> Kostüme.', 'He wore <em>various</em> costumes.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2025, 1, 'Wir dürfen die Gefahren nicht <em>unterschätzen</em>.', 'We must not <em>underestimate</em> the dangers.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2025, 2, 'Er hat ihr Talent immer <em>unterschätzt</em>.', 'He always <em>undervalued</em> her talent.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2026, 1, 'Er hat ihre Fähigkeiten stark <em>unterschätzt</em>.', 'He <em>underestimated</em> her abilities greatly.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2026, 2, 'Das Talent des Künstlers wurde lange <em>unterschätzt</em>.', 'The artist''s talent was long <em>underrated</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2026, 3, 'Sie wurde oft <em>unterschätzt</em> aufgrund ihres Aussehens.', 'She was often <em>misjudged</em> because of her appearance.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2027, 1, 'Die <em>Untertänigkeit</em> gegenüber Vorgesetzten wurde oft erwartet.', 'Subservience towards superiors was often expected.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2027, 2, 'Seine <em>Untertänigkeit</em> war übertrieben und unangenehm.', 'His obsequiousness was exaggerated and unpleasant.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2027, 3, '<em>Untertänigkeit</em> ist in einer gleichberechtigten Beziehung fehl am Platz.', 'Servility has no place in an equal relationship.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2027, 4, 'Die <em>Untertänigkeit</em> des Hundes zeigte, dass er gut erzogen war.', 'The dog''s submissiveness showed that it was well-trained.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2028, 1, 'Die Regierung <em>unterwirft</em> die Bürger strengen Kontrollen.', 'The government <em>subjects</em> the citizens to strict controls.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2028, 2, 'Der König <em>unterwarf</em> das ganze Land.', 'The king <em>subjugated</em> the entire country.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2028, 3, 'Er <em>unterwarf</em> sich dem Willen seiner Eltern.', 'He <em>submitted</em> to his parents'' wishes.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2028, 4, 'Der Lehrer <em>unterwarf</em> den Schülern zusätzliche Aufgaben.', 'The teacher <em>imposed</em> extra assignments on the students.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2029, 1, 'Der<em>unverkennbare</em>Geruch von frisch gebackenem Brot erfüllte den Raum.', 'The <em>unmistakable</em> smell of freshly baked bread filled the room.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2029, 2, 'Sein<em>unverkennbares</em>Talent zeigte sich schon in jungen Jahren.', 'His <em>distinct</em> talent showed even at a young age.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2029, 3, 'Die<em>unverkennbare</em>Ähnlichkeit zwischen den Geschwistern war sofort sichtbar.', 'The <em>indisputable</em> resemblance between the siblings was immediately apparent.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2030, 1, 'Die Person war <em>unverletzt</em> nach dem Unfall.', 'The person was <em>uninjured</em> after the accident.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2030, 2, 'Das wertvolle Gemälde blieb <em>unverletzt</em>.', 'The valuable painting remained <em>unharmed</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2030, 3, 'Er kam <em>unverletzt</em> aus dem brennenden Gebäude.', 'He came out of the burning building <em>unscathed</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2030, 4, 'Die Daten blieben <em>unverletzt</em> während des Stromausfalls.', 'The data remained <em>intact</em> during the power outage.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2031, 1, 'Der Fahrer blieb <em>unverletzt</em> nach dem Unfall.', 'The driver remained <em>uninjured</em> after the accident.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2031, 2, 'Die Feuerwehr rettete mehrere <em>Unverletzte</em> aus dem brennenden Gebäude.', 'The firefighters rescued several <em>uninjured people</em> from the burning building.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2031, 3, 'Nach dem Erdbeben gab es viele <em>Unverletzte</em>, die gerettet werden mussten.', 'After the earthquake, there were many <em>survivors</em> who needed to be rescued.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2032, 1, 'Sein <em>unvorsichtig</em>es Verhalten führte zu einem Unfall.', 'His <em>careless</em> behavior led to an accident.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2032, 2, 'Sie machte <em>unvorsichtige</em> Entscheidungen in ihrer Jugend.', 'She made <em>reckless</em> decisions in her youth.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2032, 3, 'Das war eine <em>unvorsichtige</em> Bemerkung.', 'That was a <em>rash</em> remark.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2032, 4, 'Sei <em>unvorsichtig</em> und pass auf, wo du hintrittst!', 'Be <em>inattentive</em> and watch where you''re stepping!');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2033, 1, 'Die <em>unvorstellbare</em> Größe des Universums überwältigt viele Menschen.', 'The <em>unimaginable</em> size of the universe overwhelms many people.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2033, 2, 'Für sie war es <em>unvorstellbar</em>, ohne ihre Familie zu leben.', 'For her, it was <em>inconceivable</em> to live without her family.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2033, 3, 'Seine <em>unvorstellbare</em> Grausamkeit schockte alle.', 'His <em>incomprehensible</em> cruelty shocked everyone.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2033, 4, 'Ein Zusammenbruch der Wirtschaft ist für viele die <em>unvorstellbare</em> Katastrophe.', 'An economic collapse is, for many, the <em>unthinkable</em> disaster.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2034, 1, 'Ein <em>unwichtig</em>es Detail wurde übersehen.', 'An <em>unimportant</em> detail was overlooked.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2034, 2, 'Es ist nur eine <em>unwichtige</em> Frage.', 'It''s just an <em>insignificant</em> question.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2034, 3, 'Sein rauschen war <em>unwichtig</em>.', 'Its noise was <em>not important</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2034, 4, 'Sie macht sich über so <em>unwichtige</em> Dinge Sorgen.', 'She worries about such <em>trivial</em> things.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2035, 1, 'Die Szenerie war <em>unwirklich</em> schön.', 'The scene was <em>unreal</em> in its beauty.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2035, 2, 'Er hatte einen <em>unwirklichen</em> Blick in seinen Augen.', 'He had a <em>surreal</em> look in his eyes.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2035, 3, 'Die <em>unwirklichen</em> Geschichten konnten ihn nicht einschüchtern.', 'The <em>fictitious</em> stories could not intimidate him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2036, 1, 'Der Regen <em>unziert</em> schnell in der Sonne.', 'The rain <em>evaporates</em> quickly in the sun.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2036, 2, 'Die Pfütze <em>unziert</em> nach ein paar Stunden.', 'The puddle <em>evaporates</em> after a few hours.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2036, 3, 'Sein Mut <em>unziert</em> wie der Nebel am Morgen.', 'His courage <em>disappears</em> like the fog in the morning.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2037, 1, 'Unzweifelhaft <em>hat</em> sie die Aufgabe richtig gelöst.', '<em>Undoubtedly</em> she solved the task correctly.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2037, 2, 'Der Fortschritt ist <em>unzweifelhaft</em> sichtbar.', 'The progress is <em>undeniably</em> visible.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2037, 3, '<em>Unzweifelhaft</em> gehört dieser Weg zu den besten.', '<em>Incontrovertibly</em>, this path is one of the best.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2038, 1, 'Sie hatte <em>unzählige</em> Bücher in ihrer Sammlung.', 'She had <em>countless</em> books in her collection.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2038, 2, 'Er machte <em>unzählige</em> Versuche, das Problem zu lösen.', 'He made <em>innumerable</em> attempts to solve the problem.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2038, 3, 'Die Sterne am Himmel sind <em>unzählige</em>.', 'The stars in the sky are <em>myriad</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2038, 4, 'Die <em>unzähligen</em> Möglichkeiten überwältigten sie.', 'The <em>endless</em> possibilities overwhelmed her.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2039, 1, 'Die beiden Gemälde sind <em>unähnlich</em> in Stil und Farbe.', 'The two paintings are <em>dissimilar</em> in style and color.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2039, 2, 'Sie haben <em>unähnliche</em> Charaktere.', 'They have <em>unlike</em> personalities.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2039, 3, 'Die Ergebnisse sind <em>unähnlich</em> und nicht vergleichbar.', 'The results are <em>not similar</em> and not comparable.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2040, 1, 'Er kam <em>urplötzlich</em> in den Raum.', 'He came into the room <em>all of a sudden</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2040, 2, '<em>Urplötzlich</em> begann es zu regnen.', '<em>Suddenly</em> it started to rain.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2040, 3, '<em>Urplötzlich</em> war sie verschwunden.', 'She had vanished <em>out of the blue</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2040, 4, '<em>Urplötzlich</em> hörte die Musik auf.', '<em>Without warning</em>, the music stopped.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2041, 1, 'Die <em>Ursache</em> des Problems ist unbekannt.', 'The <em>cause</em> of the problem is unknown.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2041, 2, 'Er wollte wissen, welche <em>Ursache</em> zu ihrer Entscheidung führte.', 'He wanted to know what the <em>reason</em> was for her decision.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2041, 3, 'Die <em>Ursache</em> der Krankheit liegt in einem genetischen Defekt.', 'The <em>source</em> of the disease lies in a genetic defect.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2042, 1, 'Die <em>vage</em> Erinnerung an die Kindheit blieb in ihrem Kopf.', 'The <em>vague</em> memory of childhood remained in her mind.');
