INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2166, 3, 'Die <em>voraussehende</em> Technologie revolutionierte die Branche.', 'The <em>anticipatory</em> technology revolutionized the industry.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2166, 4, 'Ihr <em>voraussehendes</em> Gefühl warnte sie vor dem Unglück.', 'Her <em>precognitive</em> feeling warned her of the disaster.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2167, 1, 'Der<em> vorbedachte </em>Plan <em>scheiterte</em> aufgrund unvorhergesehener Umstände.', 'The <em>premeditated</em> plan <em>failed</em> due to unforeseen circumstances.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2167, 2, 'Es war keine zufällige Handlung, sondern eine <em>vorbedachte</em> Entscheidung.', 'It wasn''t a random act, but a <em>deliberate</em> decision.');
