INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2169, 4, 'Er wollte nicht lange bleiben, nur schnell <em>vorbeikommen</em>.', 'He didn''t want to stay long, just wanted to <em>drop by</em> quickly.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2170, 1, 'Er fühlte sich <em>vorbestimmt</em>, eine große Entdeckung zu machen.', 'He felt <em>predestined</em> to make a great discovery.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2170, 2, 'Ihr <em>vorbestimmtes</em> Schicksal war ihm schon lange klar.', 'Her <em>preordained</em> fate had been clear to him for a long time.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2170, 3, 'Die Regeln waren <em>vorbestimmt</em> und konnten nicht geändert werden.', 'The rules were <em>determined in advance</em> and could not be changed.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2170, 4, 'Er hatte eine <em>vorbestimmte</em> Rolle in der Geschichte.', 'He had a <em>fixed</em> role in the story.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2171, 1, 'Er <em>bringt</em> einen interessanten Vorschlag <em>vor</em>.', 'He <em>puts forward</em> an interesting proposal.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2171, 2, 'Sie <em>brachte</em> ihre Argumente klar <em>vor</em>.', 'She <em>presented</em> her arguments clearly.');
