INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2172, 1, 'Der <em>voreilige</em> Schluss wurde schnell von Experten korrigiert.', 'The <em>premature</em> conclusion was quickly corrected by experts.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2172, 2, 'Eine <em>voreilige</em> Entscheidung kann Probleme verursachen.', 'A <em>hasty</em> decision can cause problems.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2172, 3, 'Sein <em>voreiliger</em> Kommentar führte zu Missverständnissen.', 'His <em>rash</em> comment led to misunderstandings.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2173, 1, 'Wir müssen jetzt <em>vorgehen</em> und die notwendigen Schritte unternehmen.', 'We need to <em>proceed</em> now and take the necessary steps.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2173, 2, 'Das Team hat beschlossen, trotz der Probleme <em>vorzugehen</em>.', 'The team decided to <em>go ahead</em> despite the problems.');
