INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2174, 1, 'Das Budget ist <em>vorgesehen</em> für neue Projekte.', 'The budget is <em>planned</em> for new projects.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2174, 2, 'Das Geschenk war <em>vorgesehen</em> für ihren Geburtstag.', 'The gift was <em>intended</em> for her birthday.');
