INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2174, 4, 'Der Termin war ursprünglich für nächsten Dienstag <em>vorgesehen</em>.', 'The appointment was originally <em>scheduled</em> for next Tuesday.');
