INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2175, 2, 'Er <em>ging entschlossen vor</em>.', 'He <em>took decisive action</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2175, 3, 'Die Technik <em>ist</em> in den letzten Jahren stark <em>vorgegangen</em>.', 'Technology has <em>advanced</em> significantly in recent years.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2175, 4, 'Sie <em>ging</em> mutig <em>vor</em>.', 'She <em>moved forward</em> bravely.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2176, 1, 'Es sind keine <em>vorhandenen</em> Ressourcen mehr.', 'There are no <em>available</em> resources left.');
