INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2177, 1, 'Das <em>Vorhandensein</em> von Beweisen ist entscheidend.', 'The <em>presence</em> of evidence is crucial.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2177, 2, 'Das <em>Vorhandensein</em> von Wasser ist für das Leben notwendig.', 'The <em>existence</em> of water is essential for life.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2177, 3, 'Das <em>Vorhandensein</em> von Ressourcen muss gewährleistet werden.', 'The <em>availability</em> of resources must be ensured.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2177, 4, 'Das <em>Vorhandensein</em> von Mängeln wurde sofort erkannt.', 'The <em>presence</em> of defects was immediately noticed.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2178, 1, 'Sie zog den <em>Vorhang</em> auf, um das Licht hereinzulassen.', 'She <em>pulled the curtain</em> back to let the light in.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2178, 2, 'Der <em>Vorhang</em> fiel majestätisch von der Bühne.', 'The <em>drape</em> fell majestically from the stage.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2178, 3, 'Der <em>Vorhang</em> schützt das Publikum vor dem grellen Licht.', 'The <em>screen</em> protects the audience from the bright light.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2179, 1, 'Vorher hatten wir noch nie miteinander gesprochen.', 'Before, we had never spoken to each other.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2179, 2, 'Ich habe ihn vorher gesehen.', 'I saw him earlier.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2179, 3, 'Hast du das vorher gecheckt?', 'Did you check that beforehand?');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2179, 4, 'Der Bericht war vorher fertig.', 'The report was finished previously.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2180, 1, 'Die <em>vorhergegangenen</em> Ereignisse waren entscheidend für das Ergebnis.', 'The <em>previous</em> events were crucial for the outcome.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2180, 2, 'In den <em>vorhergegangenen</em> Monaten gab es viele Diskussionen.', 'In the <em>preceding</em> months, there were many discussions.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2180, 3, 'Die <em>vorhergegangenen</em> Abschnitte des Berichts sind wichtig.', 'The <em>earlier</em> sections of the report are important.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2181, 1, 'In der <em>vorherigen</em> Sitzung haben wir beschlossen, die Änderungen zu überprüfen.', 'In the <em>previous</em> meeting, we decided to review the changes.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2181, 2, 'Sie hat <em>vorherige</em> Erfahrungen in ähnlichen Projekten gesammelt.', 'She gained <em>earlier</em> experience in similar projects.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2181, 3, 'Vor der <em>vorherigen</em> Wahl gab es viele Debatten.', 'Before the <em>prior</em> election, there were many debates.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2182, 1, 'Das Wetter ist immer <em>vorhersehbar</em> im Winter.', 'The weather is always <em>predictable</em> in winter.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2182, 2, 'Die <em>vorhersehbaren</em> Konsequenzen wurden ignoriert.', 'The <em>foreseeable</em> consequences were ignored.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2182, 3, 'Seine Reaktionen sind <em>vorhersehbar</em>.', 'His reactions are <em>predictable</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2183, 1, 'Stellen Sie die Blumen <em>vorn</em> auf den Tisch.', 'Put the flowers <em>at the front</em> on the table.');
