INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2184, 1, 'Der Parkplatz ist <em>vorne</em>.', 'The parking lot is <em>in front</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2184, 2, 'Sie sitzt <em>vorne</em> im Bus.', 'She sits <em>at the front</em> of the bus.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2184, 3, 'Das Ziel ist gleich <em>vorne</em>.', 'The goal is just <em>ahead</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2184, 4, 'Schau <em>vorne</em>!', 'Look <em>forward</em>!');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2185, 1, '<em>Vorneweg</em> möchte ich sagen, dass ich <em>vorneweg</em> nicht zustimmen kann.', 'To begin with, I want to say that I can''t agree from the outset.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2185, 2, '<em>Vor</em> der Diskussion sollten wir <em>vorneweg</em> einige Punkte klären.', 'First of all, we should clarify some points before the discussion.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2185, 3, 'Sie hat <em>vorneweg</em> gewarnt, dass es schwierig werden könnte.', 'She warned right away that it could be difficult.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2185, 4, '<em>Vor</em> dem Experiment sollten wir <em>vorneweg</em> die Sicherheitsvorkehrungen überprüfen.', 'From the outset of the experiment, we should check the safety precautions.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2186, 1, 'Die Regierung hat viele <em>Vorsorgemaßnahmen</em> getroffen, um die Gesundheit der Bevölkerung zu schützen.', 'The government has taken many <em>preventive measures</em> to protect public health.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2186, 2, 'Wir sollten <em>Vorsorgemaßnahmen</em> ergreifen, um Unfälle zu vermeiden.', 'We should take <em>precautionary measures</em> to avoid accidents.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2186, 3, 'Die Firma implementierte mehrere <em>Vorsorgemaßnahmen</em>, um Datenverluste zu verhindern.', 'The company implemented several <em>safeguards</em> to prevent data loss.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2187, 1, 'Ich <em>stelle</em> dir meinen Freund vor.', 'I <em>introduce</em> my friend to you.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2187, 2, 'Sie <em>stellte</em> ihr neues Projekt <em>vor</em>.', 'She <em>presented</em> her new project.');
