INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2187, 4, 'Die <em>Vorstellung</em> beginnt um acht Uhr.', 'The <em>performance</em> starts at eight o''clock.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2188, 1, 'Die <em>Vorstellung</em> begann pünktlich um acht Uhr.', 'The <em>presentation</em> started promptly at eight o''clock.');
