INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2188, 3, 'Ihre <em>Vorstellung</em> von Erfolg war anders als meine.', 'Her <em>idea</em> of success was different from mine.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2188, 4, 'In ihrer <em>Vorstellung</em> lebten die Tiere glücklich zusammen.', 'In her <em>imagination</em>, the animals lived happily together.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2189, 1, 'Sie ging <em>vorwärts</em>, ohne zurückzuschauen.', 'She walked <em>forwards</em>, not looking back.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2189, 2, 'Der Schalter bewegt sich <em>vorwärts</em>.', 'The switch moves <em>forward</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2189, 3, 'Der Plan sieht vor, das Projekt <em>vorwärts</em> zu bringen.', 'The plan aims to push the project <em>ahead</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2189, 4, 'Die Zeit vergeht <em>vorwärts</em>.', 'Time moves <em>forwards</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2190, 1, 'Sie <em>ver</em>eiste die Früchte, um sie später zu verwenden.', 'She <em>froze</em> the fruits to use them later.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2190, 2, 'Im Winter <em>ver</em>eist der Teich.', 'In winter, the pond <em>freezes</em> over.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2190, 3, 'Die Sprinkleranlage <em>ver</em>eiste in der Kälte.', 'The sprinkler system <em>froze</em> in the cold.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2191, 1, 'Die <em>Vögel</em> singen fröhlich in den Bäumen.', 'The <em>birds</em> sing happily in the trees.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2191, 2, 'Es gibt viele verschiedene Arten von <em>Vögeln</em> in dieser Region.', 'There are many different species of <em>birds</em> in this region.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2191, 3, 'Die <em>Vögel</em> flogen über das Feld.', 'The <em>birds</em> flew over the field.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2191, 4, 'Laut <em>Vögel</em> gibt es in diesem Park schöne <em>Vögel</em> zu sehen.', 'According to reports, there are beautiful <em>birds</em> to see in this park.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2192, 1, 'Sie <em>wacht</em> jede Nacht <em>über</em> die schlafenden Kinder.', 'She <em>keeps watch</em> over the sleeping children every night.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2192, 2, 'Wir müssen <em>darauf</em> <em>wachen</em>, dass er nicht zu viel arbeitet.', 'We need to <em>keep an eye on</em> him to make sure he doesn''t overwork.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2192, 3, 'Die Forscher <em>wachen</em> sorgfältig <em>über</em> das Experiment.', 'The researchers <em>supervise</em> the experiment carefully.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2193, 1, 'Der <em>wachsame</em> Hund bemerkte sofort den Eindringling.', 'The <em>vigilant</em> dog immediately noticed the intruder.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2193, 2, 'Die <em>wachsame</em> Beobachterin sah jedes Detail genau.', 'The <em>alert</em> observer noticed every detail carefully.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2193, 3, 'Dank ihrer <em>wachsame</em> Natur entging ihr nichts.', 'Thanks to her <em>attentive</em> nature, she missed nothing.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2193, 4, 'Die Eltern blieben <em>wachsam</em>, während die Kinder spielten.', 'The parents remained <em>watchful</em> while the children played.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2194, 1, 'Die Pflanzen <em>wachsen</em> schnell in der Sonne.', 'The plants <em>grow</em> quickly in the sun.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2194, 2, 'Die Bevölkerung <em>wächst</em> jedes Jahr.', 'The population <em>increases</em> every year.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2194, 3, 'Die Flüsse <em>wachsen</em> nach dem Regen.', 'The rivers <em>rise</em> after the rain.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2195, 1, 'Die <em>wachsende</em> Nachfrage nach erneuerbaren Energien ist ein gutes Zeichen.', 'The <em>growing</em> demand for renewable energy is a good sign.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2195, 2, 'Die <em>wachsende</em> Belastung des Gesundheitssystems muss dringend adressiert werden.', 'The <em>increasing</em> burden on the healthcare system urgently needs to be addressed.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2195, 3, 'Das Unternehmen hat seine <em>wachsende</em> Präsenz auf dem internationalen Markt verstärkt.', 'The company has strengthened its <em>expanding</em> presence in the international market.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2195, 4, 'Sie beobachtete den <em>wachsenden</em> Baum im Garten.', 'She watched the <em>growing</em> tree in the garden.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2195, 5, 'Die <em>wachsende</em> Zahl von Nutzern hat die Entwicklung neuer Funktionen erforderlich gemacht.', 'The <em>expanding</em> number of users has made the development of new features necessary.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2196, 1, 'Er stellte die Gewehre in den <em>Waffenständer</em>.', 'He placed the rifles in the <em>gun rack</em>.');
