INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2331, 1, 'Der <em>Zimtgeruch</em> erfüllte das ganze Zimmer.', 'The <em>cinnamon scent</em> filled the entire room.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2331, 2, 'Der <em>Zimtgeruch</em> erinnerte sie an ihre Kindheit.', 'The <em>cinnamon smell</em> reminded her of her childhood.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2331, 3, 'Er liebte den <em>Zimtgeruch</em> in der Luft.', 'He loved the <em>smell of cinnamon</em> in the air.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2332, 1, 'Das <em>Zischgeräusch</em> des Wassers beim Kochen war deutlich zu hören.', 'The <em>hissing sound</em> of the water boiling was clearly audible.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2332, 2, 'Ein seltsames <em>Zischgeräusch</em> kam aus dem Kühlschrank.', 'A strange <em>hiss</em> was coming from the refrigerator.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2332, 3, 'Das <em>Zischgeräusch</em> von gebratenem Fleisch machte jeden hungrig.', 'The <em>sizzling noise</em> of fried meat made everyone hungry.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2332, 4, 'Als der Dampf entwich, gab es ein lautes <em>Zischgeräusch</em>.', 'When the steam escaped, there was a loud <em>hiss sound</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2333, 1, 'Das <em>Zitat</em> stammt von einem berühmten Autor.', 'The <em>quote</em> originates from a famous author.');
