INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2333, 3, 'Im <em>Zitat</em> heißt es: ''Sei die Veränderung, die du in der Welt sehen möchtest.''', 'The <em>quotation</em> says: ''Be the change you wish to see in the world.''');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2334, 1, 'Der Doktor <em>zitiert</em> eine wichtige Studie.', 'The doctor <em>quotes</em> an important study.');
