INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2338, 4, 'Er <em>zuckte</em> bei dem lauten Geräusch zusammen.', 'He <em>gave a start</em> at the loud noise.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2339, 1, 'Er hat viel gelernt; <em>zudem</em> hat er die Praxis nicht vernachlässigt.', 'He studied a lot; <em>in addition</em>, he didn''t neglect practical experience.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2339, 2, '<em>Zudem</em> ist das eine sehr teure Lösung.', '<em>Besides</em>, that''s a very expensive solution.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2339, 3, '<em>Zudem</em> gibt es zahlreiche gesundheitliche Vorteile.', '<em>Furthermore</em>, there are numerous health benefits.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2339, 4, '<em>Zudem</em> muss man die Risiken berücksichtigen.', '<em>Moreover</em>, one must consider the risks.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2340, 1, '<em>Zuerst</em> haben wir gefrühstückt, <em>dann</em> sind wir spazieren gegangen.', '<em>First</em> we had breakfast, <em>then</em> we went for a walk.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2340, 2, '<em>Zuerst</em> war ich skeptisch, aber dann hat es mir doch gefallen.', '<em>At first</em> I was skeptical, but then I actually liked it.');
