INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2341, 1, 'Es war reiner <em>Zufall</em>, dass wir uns trafen.', 'It was pure <em>chance</em> that we met.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2341, 2, 'Es war kein <em>Zufall</em>, dass er genau an diesem Tag kam.', 'It was no <em>coincidence</em> that he came exactly on that day.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2341, 3, 'Die <em>Zufälle</em> des Lebens sind oft faszinierend.', 'The <em>randomness</em> of life is often fascinating.');
