INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2342, 2, 'Die <em>zufriedenen</em> Kunden empfehlen das Produkt weiter.', 'The <em>content</em> customers recommend the product further.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2342, 3, 'Nach dem Gespräch fühlte sie sich <em>zufrieden</em>.', 'After the conversation, she felt <em>happy</em>.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2343, 1, 'Es war reiner <em>Zufall</em>, dass sie sich getroffen haben.', 'It was pure <em>coincidence</em> that they met.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2343, 2, 'Die <em>Zufälligkeiten</em> des Lebens sind oft unerklärlich.', 'The <em>randomness</em> of life is often inexplicable.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2343, 3, 'Solche <em>Zufälligkeiten</em> können jedem passieren.', 'Such <em>chance events</em> can happen to anyone.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2343, 4, 'Die <em>Zufälligkeit</em> der Entdeckung war außergewöhnlich.', 'The <em>accident</em> of the discovery was extraordinary.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2344, 1, 'Er hat dem Bericht neue Informationen <em>zugefügt</em>.', 'He <em>added</em> new information to the report.');
