INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2344, 3, 'Sie <em>fügte</em> dem Dokument ihren Lebenslauf <em>zu</em>.', 'She <em>attached</em> her resume to the document.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2344, 4, 'Er hat der Diskussion eine interessante Perspektive <em>zugefügt</em>.', 'He <em>provided</em> an interesting perspective to the discussion.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2345, 1, 'Das Autorenrecht ist <em>zugehörig</em> zum Buch.', 'The copyright is <em>belonging</em> to the book.');
