INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2351, 1, 'Zunächst <em>war</em> alles ruhig.', 'Initially, <em>everything was</em> quiet.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2351, 2, '<em>Zunächst</em> dachte ich, es wäre einfach.', 'At first, I <em>thought</em> it would be easy.');
